<?php
header ( 'Content-type:text/html;charset=utf-8' );
include_once $_SERVER ['DOCUMENT_ROOT'] . '/upacp_demo_app/sdk/acp_service.php';

/**
 * 对控件给商户APP返回的应答信息验签，前段请直接把string型的json串post上来
 */
$data = file_get_contents('php://input', 'r');
echo validateAppResponse($data) ? "true" : "false";

/**
 * 对控件支付成功返回的结果信息中data域进行验签
 * @param $jsonData json格式数据，例如：{"sign" : "",  "data" : "pay_result=success&tn=201602141008032671528&cert_id=68759585097"}
 * @return 是否成功
 */
function validateAppResponse($jsonData) {

	$data = json_decode($jsonData);
	$sign = $data->sign;
	$data = $data->data;
    $public_key = openssl_x509_read(file_get_contents("/data/wwwroot/lebo/api/public/upacp_demo_app/certs/acp_test_app_verify_sign.cer"));//TODO，这个是测试环境的证书，切换生产需要改生产证书。
	$signature = base64_decode ( $sign );
	$params_sha1x16 = sha1 ( $data, FALSE );
	$isSuccess = openssl_verify ( $params_sha1x16, $signature,$public_key, OPENSSL_ALGO_SHA1 );
	return $isSuccess;
}
