<?php	require_once '../config.php';	//接收参数订单成功数据请求	//http://charge.860051.cn/reward/	//{ "orderid": "20180705214512985412", "openid": "oEsQlwRwCEE1DnoLEOauowZEU3d4", "money": 100 }	if (isset($_POST['receipt_data']) && $_POST['receipt_data'] != '') {		$postdata = json_decode($_POST['receipt_data'], true);		$reward_param = OpensslEncryptHelper::decryptWithOpenssl($postdata);		file_put_contents("/data/wwwroot/lebo/charge/post.txt",print_r($reward_param,true)."\r\n".$_POST['receipt_data'],FILE_APPEND);		if(isset($reward_param['money']) && $reward_param['money']!='' && isset($reward_param['orderid']) && $reward_param['orderid']!='' && isset($reward_param['openid']) && $reward_param['openid']!=''){			require_once 'reward.class.php';			$reward = new Reward();			$result = $reward->init($reward_param);			echo $result;exit();		}else{			echo json_decode(array('result' => '参数错误'));exit();		}	} else {		echo json_decode(array('result' => '参数错误'));exit();	}